<script>
    //
    let lname = document.querySelector("#l_name");
    let mname = document.querySelector("#m_name");
    let fname = document.querySelector("#f_name");
    let name = document.querySelector("#name");
    //
    [lname,mname,fname].forEach(element => {
        element.addEventListener('input', function(){
            name.value = lname.value.toUpperCase() + ', ' + mname.value.toUpperCase() +' ' + fname.value.toUpperCase();
        });
    });
</script>
